import react,{useState} from 'react'
import Instructionpan from './Instructionpan'
import Emojidata from './Emojidata'
import Emgcard from './Emgcard'
import Gameplay from './Gameplay'
import './Body.css'
const Body=()=>{
const [val1,setVal]=useState(-1);
const [cng,setCng]=useState(false);
const cngState=()=>{
    if(val1==-1)
    alert("Please select any emoji... ;) ");
    else if(cng==false)
    setCng(true);
    else
    setCng(false);
    }
return(
    <>
<div id="Body">

<Instructionpan
Iindex={val1}
/>

<div id="innerBody">

{cng==true?Emojidata.map((val,index)=>{
const setIndex=()=>{
    setVal(index);
}
return(
<div id="EmgCard" style={{display:"flex",flexDirection:"column",color:"white"}} onClick={setIndex}>
    <Emgcard
    Emoji={val}
    Eindex={index}
    />
</div>
    )

}):
<Gameplay/>
}
</div>

<div id="btnContainer">
<h2 style={{color:"white"}} onClick={cngState}>{cng==true?"Start":"Next"}</h2>
</div>

</div>
</>
)
}
export default Body
