import react,{useState} from 'react'
import Instructionpan from './Instructionpan'
import Emojidata from './Emojidata'
import Emgcard from './Emgcard'
import Gameplay from './Gameplay'
import './Body.css'
const Body=()=>{
const [val1,setVal]=useState(0);
const [cng,setCng]=useState(false);
const cngState=()=>{
    if(cng==0)
    setCng(true);
    else
    setCng(false);
    }
return(
    <>
<div id="Body">

<Instructionpan
Iindex={val1}
/>

<div id="innerBody">
{Emojidata.map((val,index)=>{
const setIndex=()=>{
    setVal(index);
}
if(cng==false)
{
return(
<div id="EmgCard" style={{display:"flex",flexDirection:"column",color:"white"}} onClick={setIndex}>
    <Emgcard
    Emoji={val}
    Eindex={index}
    />
</div>
    )
}
else{
    return <Gameplay/>
}
})}
</div>

<div id="btnContainer">
<h2 style={{color:"white"}} onClick={cngState}>Next</h2>
</div>

</div>
</>
)
}
export default Body
