import react,{useState} from 'react'
import Instructionpan from './Instructionpan'
import Emojidata from './Emojidata'
import Emgcard from './Emgcard'
import Gameplay from './Gameplay'
//import Displayresult from './Displayresult'
import Gamealgo from './Gamealgo'
import './Body.css'
var resultData
let boundaryObj={
    position:"absolute",
    border:"3px solid rgba(238, 28, 28, 0.897)",
    zIndex:"12",
    width:"441px",
    height: "60px",
    marginTop: "164px",
    //backgroundColor:"rgba(241, 34, 34)",
    //boxShadow:"2px 2px 29px black inset"
  }
const Body=()=>{
const [val1,setVal]=useState(-1);
const [cng,setCng]=useState(false);
const [start,setStart]=useState(false);
const cngState=()=>{
    if(val1==-1)
    alert("Please select any emoji... ;) ");
    else if(cng==false){
    setCng(true);
    }
    else{
    setStart(true);
    }
}
return(
    <>
<div id="Body">
<Instructionpan
Iindex={val1}
Icng={cng}
/>

<div id="innerBody">

{cng==0?Emojidata.map((val,index)=>{
const setIndex=()=>{
    setVal(index);
}
return(
<div id="EmgCard" style={{display:"flex",flexDirection:"column",color:"white"}} onClick={setIndex}>
    <Emgcard
    Emoji={val}
    Eindex={index}
    />
</div>
    )

}):

<>
<Gameplay gameStart={start}/>
<div style={boundaryObj}>
<div style={{
    // backgroundColor:"rgba(241, 34, 34)",
     //boxShadow:"2px 2px 29px black inset",
     zIndex:"10",
     position:"absolute",
     width:"441px",
     height: "61px",
     marginTop:"-3px"
}}>
</div>
<Gamealgo startGame={start} selectedEmoji={val1}/>
</div>
</>
}
</div>
<div id="btnContainer" onClick={cngState}>
<h2 style={{color:"white"}}>{cng==0?"Next":"Start"}</h2>
</div>
</div>
</>
)
}
export default Body
