import react,{useState} from 'react'

let styleObj={
    width:"500px",
    height:"95px",
    backgroundColor: "rgba(209, 54, 54, 0.897)",
    boxShadow:  "2px 2px 29px black inset",
    padding:"29px",
    color:"White",
    textAlign:"center"
}
const Instructionpan=()=>{
    const [instr,setInstr]=useState("Select Your Emoji ;) ");
return(
    <>
<div style={styleObj}>
<h2>{instr}</h2>
</div>
    </>
)
}
export default Instructionpan
