import react,{useState} from 'react'
import Emojidata from './Emojidata'
let carrierObj={
border:"3px solid rgba(238, 28, 28, 0.897)",
height:"449px",
width:"500px",
display:"flex",
justifyContent:"space-evenly"
}
let boxObj={
    height:"445px",
    width:"95px",
    overflow:"hidden",
    backgroundColor:"rgb(240, 105, 105)"
}
const Emgcarrier=(props)=>{
  const [x,setX]=useState(4);
  if(props.start==true)
  {
    
    setInterval(()=>{
      document.getElementById('d1').scrollBy(0,x);
      console.log(document.getElementById('d1').scrollTop)
    },10)
    
  }
    return(
        <>
    <div id="d1" style={boxObj}>
        {
        Emojidata.map((val)=>{
          return(
              <>
              <span style={{fontSize:"40px",paddingLeft:"21px"}}>{val}</span>
              </>
          )
        })
        }
    </div>
    </>
    )
}
const Gameplay=(props)=>{
return(
<>
<div id="carrier" style={carrierObj}>
<Emgcarrier start={props.gameStart}/>
<Emgcarrier start={props.gameStart}/>
<Emgcarrier start={props.gameStart}/>
</div>
</>
)
}
export default Gameplay
