import react,{useState} from 'react'
import Emojidata from './Emojidata'
let carrierObj={
border:"3px solid rgba(238, 28, 28, 0.897)",
height:"449px",
width:"500px",
display:"flex",
justifyContent:"space-evenly"
}
let boxObj={
    height:"445px",
    width:"95px",
    overflow:"hidden",
    backgroundColor:"rgb(240, 105, 105)",
    zIndex:"5"
}
let boundaryObj={
  position:"absolute",
  zIndex:"5",
  border:"3px solid rgba(238, 28, 28, 0.897)",
  width:"441px",
  height:"55px",
  marginTop:"163px",
  backgroundColor:"rgba(241, 34, 34, 0.897)",
  boxShadow:"2px 2px 29px black inset"
}
const Emgcarrier=(props)=>{
  if(props.start==true)
  {
    setInterval(()=>{
    var endX=setInterval(()=>{
      document.getElementById('d1').scrollBy(0,1);
      if(document.getElementById('d1').scrollTop>=1692){
        clearInterval(endX) 
        var endX1=setInterval(()=>{
          document.getElementById('d1').scrollBy(0,-1);
          if(document.getElementById('d1').scrollTop==0)
          clearInterval(endX1) 
        },10)
      }
    },10)
  },12)
  setInterval(()=>{
    document.getElementById('d2').scrollTop=1692;
    var endX=setInterval(()=>{
      document.getElementById('d2').scrollBy(0,-1);
      if(document.getElementById('d2').scrollTop==0){
        clearInterval(endX) 
        var endX1=setInterval(()=>{
          document.getElementById('d2').scrollBy(0,1);
          if(document.getElementById('d2').scrollTop>=1692)
          clearInterval(endX1) 
        },10)
      }
    },10)
  },12)
  setInterval(()=>{
    var endX=setInterval(()=>{
      document.getElementById('d3').scrollBy(0,1);
      if(document.getElementById('d3').scrollTop>=1000){
        clearInterval(endX) 
        var endX1=setInterval(()=>{
          document.getElementById('d3').scrollBy(0,-1);
          if(document.getElementById('d3').scrollTop==0)
          clearInterval(endX1) 
        },10)
      }
    },10)
  },12)
  }
    return(
        <>
      <div id="d1" style={boxObj}>
     {
        Emojidata.map((val)=>{
          return(
              <>
              <span style={{fontSize:"40px",paddingLeft:"21px",zIndex:"15"}}>{val}</span>
              </>
          )
        })
        } 
      </div>
      <div id="d2" style={boxObj}>
     {
        Emojidata.map((val)=>{
          return(
              <>
              <span style={{fontSize:"40px",paddingLeft:"21px"}}>{val}</span>
              </>
          )
        })
        } 
      </div>
      <div id="d3" style={boxObj}>
     {
        Emojidata.map((val)=>{
          return(
              <>
              <span style={{fontSize:"40px",paddingLeft:"21px"}}>{val}</span>
              </>
          )
        })
        } 
      </div>
    </>
    )
}
const Gameplay=(props)=>{
return(
<>
<div id="carrier" style={carrierObj}>
<div style={boundaryObj}>
</div>
<Emgcarrier start={props.gameStart}/>
</div>
</>
)
}
export default Gameplay
